"use client";

import * as React from "react";
import { motion } from "framer-motion";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const slideCardStackVariants = cva("relative inline-block", {
  variants: {
    size: {
      sm: "h-32 w-56",
      default: "h-44 w-72",
      lg: "h-56 w-80",
      xl: "h-64 w-96",
    },
    variant: {
      default: "",
      subtle: "",
      elevated: "",
    },
  },
  defaultVariants: {
    size: "default",
    variant: "default",
  },
});

const cardVariants = cva(
  "absolute inset-0 m-auto rounded-2xl border p-6 shadow-xl transition-colors",
  {
    variants: {
      variant: {
        default: "border-border bg-card text-card-foreground",
        subtle: "border-border/50 bg-muted text-muted-foreground",
        elevated: "border-border bg-background text-foreground shadow-2xl",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
);

export interface SlideCardStackCard {
  title?: string;
  description?: string;
  content?: React.ReactNode;
  avatar?: React.ReactNode;
}

export interface SlideCardStackProps
  extends Omit<
      React.HTMLAttributes<HTMLDivElement>,
      | "onDrag"
      | "onDragStart"
      | "onDragEnd"
      | "onAnimationStart"
      | "onAnimationEnd"
    >,
    VariantProps<typeof slideCardStackVariants> {
  cards?: SlideCardStackCard[];
  disabled?: boolean;
  rotationIntensity?: number;
  springConfig?: {
    stiffness?: number;
    damping?: number;
  };
}

const defaultCards: SlideCardStackCard[] = [
  {
    title: "Bottom Card",
    description: "Tilts up on hover",
  },
  {
    title: "Middle Card",
    description: "Tilts down on hover",
  },
  {
    avatar: <div className="bg-muted h-10 w-10 rounded-full" />,
    content: (
      <>
        <div className="bg-muted mt-3 h-3 w-12 rounded-md" />
        <div className="bg-muted/70 mt-5 h-3 rounded-sm" />
        <div className="bg-muted/70 mt-2 h-3 rounded-sm" />
      </>
    ),
  },
];

const SlideCardStack = React.forwardRef<HTMLDivElement, SlideCardStackProps>(
  (
    {
      className,
      size,
      variant,
      cards = defaultCards,
      disabled = false,
      rotationIntensity = 6,
      springConfig = { stiffness: 300, damping: 20 },
      ...props
    },
    ref
  ) => {
    const motionVariants = {
      topCard: {
        rest: { rotate: 0 },
        hover: { rotate: 0 },
      },
      middleCard: {
        rest: { rotate: 0 },
        hover: { rotate: rotationIntensity },
      },
      bottomCard: {
        rest: { rotate: 0 },
        hover: { rotate: -rotationIntensity },
      },
    };

    const cardOrder = ["bottomCard", "middleCard", "topCard"] as const;
    const translateYValues = [
      "translate-y-2",
      "translate-y-1",
      "translate-y-0",
    ];

    return (
      <motion.div
        ref={ref}
        initial="rest"
        whileHover={disabled ? "rest" : "hover"}
        className={cn(slideCardStackVariants({ size }), className)}
        {...props}
      >
        {cards.slice(0, 3).map((card, index) => {
          const cardVariant = cardOrder[index];
          const translateY = translateYValues[index];

          return (
            <motion.div
              key={index}
              variants={motionVariants[cardVariant]}
              transition={{ type: "spring", ...springConfig }}
              style={{ transformOrigin: "top left" }}
              className={cn(
                cardVariants({ variant }),
                slideCardStackVariants({ size }),
                translateY,
                disabled && "cursor-default"
              )}
            >
              {card.avatar && <div className="mb-3">{card.avatar}</div>}

              {card.title && (
                <h3 className="text-lg font-semibold">{card.title}</h3>
              )}

              {card.description && (
                <p className="text-sm opacity-70">{card.description}</p>
              )}

              {card.content && <div className="mt-3">{card.content}</div>}

              {!card.content &&
                !card.avatar &&
                (card.title || card.description) && (
                  <>
                    <div className="bg-muted/70 mt-5 h-3 rounded-sm" />
                    <div className="bg-muted/70 mt-2 h-3 rounded-sm" />
                  </>
                )}
            </motion.div>
          );
        })}
      </motion.div>
    );
  }
);

SlideCardStack.displayName = "SlideCardStack";

export { SlideCardStack, slideCardStackVariants };
